declare module '*.svg' {
    export const ReactComponent: React.SFC<React.SVGProps<SVGSVGElement>>;
    const src: string;
    const content: any;
    export default content;
}

declare module '*.png' {
    const content: any;
    export default content;
}

declare module '*.jpg' {
    const content: any;
    export default content;
}

declare module '*.webp' {
    const content: any;
    export default content;
}

declare module '*.sass' {
    const content: any;
    export default content;
}

declare module '*.scss' {
    const content: any;
    export default content;
}
